export default function getUAString(): string {
  const uaData = (navigator: Navigator).userAgentData;

  if (uaData?.brands) {
    return uaData.brands
      .map((item) => `${item.brand}/${item.version}`)
      .join(' ');
  }

  return navigator.userAgent;
}
// @flow
